changeSet.sql:
CREATE TABLE book ( id varchar(36) NOT NULL CONSTRAINT book_pkey PRIMARY KEY, title varchar(255) NOT NULL, authors varchar(255) NOT NULL, isbn varchar(13) NOT NULL, publication_date TIMESTAMP NOT NULL, cover_image_url varchar(255), CONSTRAINT ck_isbn CHECK (isbn ~ '^[0-9]{13}$') );
CREATE SEQUENCE users_id_seq START 1;
CREATE TABLE users ( id BIGINT NOT NULL DEFAULT nextval('users_id_seq') CONSTRAINT users_pkey PRIMARY KEY, username varchar(100) NOT NULL, email varchar(100) NOT NULL, password varchar(255) NOT NULL );
CREATE TABLE user_books ( user_id BIGINT NOT NULL CONSTRAINT user_books_user_id_fkey REFERENCES users, book_id varchar(36) NOT NULL CONSTRAINT user_books_book_id_fkey REFERENCES book, CONSTRAINT user_books_pkey PRIMARY KEY (user_id, book_id) );
